tool.command:
	mkdir /tmp/outputdir/;
      bash /usr/local/bin/visualize-metfrag-multiple.sh $metfrag_vis_input_1 /tmp/outputdir/combined_top1_cands.csv;
      java -jar /usr/local/bin/ConvertMetFragCSV.jar csv=/tmp/outputdir/combined_top1_cands.csv output=/tmp/outputdir/combined_top1_cands.tex propertyWhiteList=Score,SampleName,Identifier,MolecularFormula,CompoundName,NumberPeaksUsed,NoExplPeaks,MetFragWebURL,parentMZ,parentRT format=tex;
      pdflatex -output-directory /tmp/outputdir/ /tmp/outputdir/combined_top1_cands.tex;
      mv /tmp/outputdir/combined_top1_cands.pdf $metfrag_vis_output_1;
      rm -rf /tmp/outputdir/;
      rm /tmp/*.png;
